  (30279, 58, 13, 22, 'And I beseech you, brethren, suffer the word of exhortation: for I have written a letter unto you in few words.', '0~1161~ 2~3870~ 3~5209~, 4~80~, 5~430~ 7~3056~ 9~3874~: 10~2532~ 11~1063~ 15~1989~ 17~5213~ 18~1223~ 20~1024~.'),
  (14286, 19, 26, 4, 'I have not sat with vain persons, neither will I go in with dissemblers.', '3~03427~ 5~07723~ 6~04962~, 11~0935~ 13~05956~.'),
