  (20927, 26, 20, 23, 'I lifted up mine hand unto them also in the wilderness, that I would scatter them among the heathen, and disperse them through the countries;', '2~05375~ 4~03027~ 10~04057~, 14~06327~ 18~01471~, 20~02219~ 24~0776~;'),
  (23405, 40, 9, 16, 'No man putteth a piece of new cloth unto an old garment, for that which is put in to fill it up taketh from the garment, and the rent is made worse.', '0~1161~ 1~3762~ 2~1911~ 4~1915~ 6~46~ 7~4470~ 8~1909~ 10~3820~ 11~2440~, 12~1063~ 21~4138~ 22~142~ 23~846~* 24~575~ 26~2440~, 27~2532~ 29~4978~ 31~1096~ 32~5501~.'),
