  (26643, 43, 12, 48, 'He that rejecteth me, and receiveth not my words, hath one that judgeth him: the word that I have spoken, the same shall judge him in the last day.', '2~114~ 3~1691~, 4~2532~ 5~2983~ 6~3361~ 7~3450~ 8~4487~, 9~2192~ 12~2919~ 13~846~: 15~3056~ 16~3739~ 19~2980~, 21~1565~ 23~2919~ 24~846~ 25~1722~ 27~2078~ 28~2250~.'),
  (28660, 46, 12, 10, 'To another the working of miracles; to another prophecy; to another discerning of spirits; to another divers kinds of tongues; to another the interpretation of tongues:', '0~1161~ 1~243~ 3~1755~ 5~1411~ 6~1161~;* 8~243~ 9~4394~ 10~1161~;* 12~243~ 13~1253~ 15~4151~ 16~1161~;* 18~2087~ [19] 20~1085~ 22~1100~ 23~1161~;* 25~243~ 27~2058~ 29~1100~:'),
  (3828, 4, 5, 31, 'Then shall the man be guiltless from iniquity, and this woman shall bear her iniquity.', '3~0376~ 5~05352~ 7~05771~, 9~01931~ 10~0802~ 12~05375~ 14~05771~.'),
