  (2630, 2, 37, 21, 'And a knop under two branches of the same, and a knop under two branches of the same, and a knop under two branches of the same, according to the six branches going out of it.', '2~03730~ 4~08147~ 5~07070~ 11~03730~ 13~08147~ 14~07070~ 20~03730~ 22~08147~ 23~07070~ 30~08337~ 31~07070~ 33~03318~'),
  (23990, 40, 24, 23, 'Then if any man shall say unto you, Lo, here is Christ, or there; believe it not.', '0~5119~ 1~1437~ 3~5100~ 5~2036~ 7~5213~, 8~2400~, 9~5602~ [10] 11~5547~, 12~2228~ 13~5602~; 14~4100~ [15] 16~3361~.'),
  (17628, 22, 6, 5, 'Turn away thine eyes from me, for they have overcome me: thy hair is as a flock of goats that appear from Gilead.', '1~05437~ 3~05869~ 7~01992~ 9~07292~ 12~08181~ [13] 16~05739~ 18~05795~ 20~01570~ 22~01568~.'),
