VALUES
  (30598, 62, 3, 3, 'And every man that hath this hope in him purifieth himself, even as he is pure.', '0~2532~ 2~3956~ 4~2192~ 5~5026~ 6~1680~ 7~1909~ 8~846~ 9~48~ 10~1438~, 12~2531~ 13~1565~ 14~2076~ 15~53~.'),
