  (6358, 6, 19, 30, 'Ummah also, and Aphek, and Rehob: twenty and two cities with their villages.', '0~05981~ 3~0663~, 5~07340~: 6~06242~ 8~08147~ 9~05892~ 12~02691~.'),
  (21780, 27, 2, 13, 'And the decree went forth that the wise men should be slain; and they sought Daniel and his fellows to be slain.', '2~01882~ 4~05312~ 7~02445~ [8] 11~06992~; 14~01156~ 15~01841~ 18~02269~ 21~06992~.');
