  (25873, 42, 21, 33, 'Heaven and earth shall pass away: but my words shall not pass away.', '0~3772~ 1~2532~ 2~1093~ 5~3928~: 6~1161~ 7~3450~ 8~3056~ 9~3928~ 10~3364~ 12~3928~.'),
  (21334, 26, 34, 12, 'As a shepherd seeketh out his flock in the day that he is among his sheep that are scattered; so will I seek out my sheep, and will deliver them out of all places where they have been scattered in the cloudy and dark day.', '2~07462~ 4~01243~ 6~05739~ 9~03117~ 13~08432~ 15~06629~ [16 17] 18~06567~; 23~01239~ 25~06629~, 28~05337~ 33~04725~ 38~06327~ 41~06051~ 43~06205~ 44~03117~.'),
  (27095, 44, 5, 20, 'Go, stand and speak in the temple to the people all the words of this life.', '0~4198~, 1~2476~ 2~2532~ 3~2980~ 4~1722~ 6~2411~ 9~2992~ 10~3956~ 12~4487~ 14~5026~ 15~2222~.'),
  (15277, 19, 84, 9, 'Behold, O God our shield, and look upon the face of thine anointed.', '0~07200~, 2~0430~ 4~04043~, 6~05027~ 9~06440~ 12~04899~.'),
