  (21127, 26, 26, 18, 'Now shall the isles tremble in the day of thy fall; yea, the isles that are in the sea shall be troubled at thy departure.', '3~0339~ 4~02729~ 7~03117~ 10~04658~; 13~0339~ [15] 18~03220~ 21~0926~ 24~03318~.'),
  (19604, 24, 26, 23, 'And they fetched forth Urijah out of Egypt, and brought him unto Jehoiakim the king; who slew him with the sword, and cast his dead body into the graves of the common people.', '3~03318~ 4~0223~ 7~04714~, 9~0935~ 12~03079~ 14~04428~; 16~05221~ 20~02719~, 22~07993~ 25~05038~ 28~06913~ 31~01121~ 32~05971~.'),
  (5070, 5, 5, 11, 'Thou shalt not take the name of the LORD thy God in vain: for the LORD will not hold him guiltless that taketh his name in vain.', '3~05375~ 5~08034~ 8~03068~ 10~0430~ 12~07723~: 15~03068~ [19] 20~05352~ 22~05375~ 24~08034~ 26~07723~.'),
