  (10806, 13, 15, 7, 'Of the sons of Gershom; Joel the chief, and his brethren an hundred and thirty:', '2~01121~ 4~01647~; 5~03100~ 7~08269~, 10~0251~ 12~03967~ 14~07970~:'),
  (16556, 20, 6, 7, 'Which having no guide, overseer, or ruler,', '3~07101~, 4~07860~, 6~04910~,'),
