  (26606, 43, 12, 11, 'Because that by reason of him many of the Jews went away, and believed on Jesus.', '0~3754~ 3~1223~ 5~846~ 6~4183~ 9~2453~ 11~5217~, 12~2532~ 13~4100~ 14~1519~ 15~2424~.'),
  (5641, 5, 28, 24, 'The LORD shall make the rain of thy land powder and dust: from heaven shall it come down upon thee, until thou be destroyed.', '1~03068~ 3~05414~ 5~04306~ 8~0776~ 9~080~ 11~06083~: 13~08064~ 17~03381~ 23~08045~.'),
